from sys import stdin, gettrace

if not gettrace():
    def input():
        return next(stdin)[:-1]


# def input():
#    return stdin.buffer.readline()

def main():
    def solve():
        n,m = map(int, input().split())
        colour = []
        for _ in range(n):
            colour.append(input())
        dir = []
        for _ in range(n):
            dir.append(input())
        eqgrid = [[None]*m for _ in range(n)]
        next_path = 0
        pathsizes = []
        eclass_has_black = []
        totls = 0
        for ni in range (n):
            for nj in range(m):
                if eqgrid[ni][nj]:
                    continue
                i = ni
                j = nj
                path = []
                count = 1
                while not eqgrid[i][j]:
                    path.append((i,j))
                    eqgrid[i][j] = (count, next_path)
                    count += 1
                    if dir[i][j] == 'L':
                        j -=1
                    elif dir[i][j] == 'R':
                        j +=1
                    elif dir[i][j] == 'U':
                        i -=1
                    else:
                        i+=1
                ppos, found_path = eqgrid[i][j]
                if found_path == next_path:
                    ls = count - ppos
                    eclass_has_black.append([False]*ls)
                    for ix, (k, l) in enumerate(path[::-1]):
                        eq = ix%ls
                        eqgrid[k][l] = (eq, next_path)
                        if colour[k][l] == '0':
                            eclass_has_black[next_path][eq] = True
                    pathsizes.append(ls)
                    totls += ls
                    next_path += 1
                else:
                    ls = pathsizes[found_path]
                    for ix, (k, l) in enumerate(path[::-1], 1):
                        eq = (ix + ppos) % ls
                        eqgrid[k][l] = (eq, found_path)
                        if colour[k][l] == '0':
                            eclass_has_black[found_path][eq] = True
        blacks = len([b for p in eclass_has_black for b in p if b])
        print(totls, blacks)




    q = int(input())
    for _ in range(q):
        solve()


if __name__ == "__main__":
    main()
