n, m = [int(x) for x in input().split()]
a = [int(x) for x in input().split()]
a.sort(reverse=True)
if sum(a) < m:
    print(-1)
else:
    l = 1
    r = n
    while l < r:
        mid = (l+r) >> 1
        s = 0
        for j in range(n):
            s += max(0, a[j] - j//mid)
        if s >= m:
            r = mid
        else:
            l = mid + 1
    else:
        print(l)
