t=int(input())
while t!=0:
    n=int(input())
    count=0
    while n>=1:
        if n%5==0:
            n=(n*4)//5
        elif n%3==0:
            n=(n*2)//3
        elif n%2==0:
            n=n//2
        else:
            break
        count+=1
    if n==1:
        print(count)
    else:
        print(-1)
    t-=1
