from sys import stdin, stdout
ti = lambda : stdin.readline().strip()
ma = lambda fxn, ti : map(fxn, ti.split())
ol = lambda arr : stdout.write(' '.join(str(i) for i in arr) + '\n')
os = lambda i : stdout.write(str(i) + '\n')
olws = lambda arr : stdout.write(''.join(str(i) for i in arr) + '\n')


s = list(ti())
slen = len(s)
snew = []
count_of_1 = 0
for i in range(slen):
	if s[i] == '1':
		count_of_1 += 1
	else:
		snew.append(s[i])

s = snew
slen = len(snew)

two_appeared = False

ans = []

for i in range(slen):
	if s[i] == '0':
		ans.append('0')
	elif s[i] == '1':
		pass
	else:
		if not two_appeared:
			for j in range(count_of_1):
				ans.append('1')
			ans.append('2')
			two_appeared = True
		else:
			ans += '2'

if not two_appeared:
	for j in range(count_of_1):
		ans.append('1')

ans = ''.join(ans)
os(ans)
