n = int(raw_input())
num = list(raw_input())
fs =  raw_input().split()
mapa = {}

for  k in xrange(9):
	mapa[str(k+1)]  = fs[k]

i = 0
while i < n and num[i] >= mapa[num[i]]:
	i += 1

j = i

while j < n and num[j] <= mapa[num[j]]:

	j += 1


for k in xrange(i, j):
	num[k] = mapa[num[k]]

print "".join(num)

