o=[]
n,q=map(int,input().split())
table = [[0 for i in range(n)] for j in range(2)]
pairs=0
for i in range(q):
    r,c=map(lambda s:int(s)-1,input().split())
    d=0
    if table[r][c]==0:
        table[r][c]=1
        d+=1
    else:
        table[r][c]=0
        d-=1

    for i in range(-1,2):
            if c+i>=0 and c+i<n and table[1-r][c+i]==1:
                pairs+=d
    if pairs==0:
        o.append('YES')
    else:
        o.append('NO')
for t in o:
    print(t)
