for _ in range(int(input())):
    a = input()
    b = input()
    l1,l2 = len(a),len(b)
    f = 1
    i,j = 0,0
    while i < l1:
        last = a[i]
        c = 0
        while i < l1 and a[i] == last:
            c += 1
            i += 1
        p = 0
        while j < l2 and b[j] == last:
            p += 1
            j += 1
        if p < c:
            f = 0
            break
    #print(i,j)
    if f and j == l2: print("YES")
    else: print("NO")
