n , k  = map(int, raw_input().split())


mas = []
for i in range(n):
    mas.append(raw_input())

places = [ ]

for i in range(len(mas)):

    if mas[i][0] == ".":
        if mas[i][1] == "S":
            places.append( [ ( i ,  0 ) , 1 ] )
        else:
            places.append([(i, 0), 0])

    if mas[i][1] == ".":
        count = 0
        if mas[i][2] == "S":
            count += 1
        if mas[i][0] == "S":
            count += 1
        places.append([(i, 1), count ])

    if mas[i][2] == ".":
        count = 0
        if mas[i][1] == "S":
            count += 1
        places.append([(i, 2), count])

    if mas[i][3+1] == ".":
        count = 0
        if mas[i][4+1] == "S":
            count += 1
        places.append([(i, 3+1), count])

    if mas[i][4+1] == ".":
        count = 0
        if mas[i][3+1] == "S":
            count += 1
        if mas[i][5+1] == "S":
            count += 1
        places.append([(i, 4+1), count])

    if mas[i][5+1] == ".":
        count = 0
        if mas[i][4+1] == "S":
            count += 1
        if mas[i][6+1] == "S":
            count += 1
        places.append([(i, 5+1), count])

    if mas[i][6+1] == ".":
        count = 0
        if mas[i][5+1] == "S":
            count += 1
        places.append([(i, 6+1), count])

    if mas[i][7+2] == ".":
        count = 0
        if mas[i][8+2] == "S":
            count += 1
        places.append([(i, 7+2), count])

    if mas[i][8+2] == ".":
        count = 0
        if mas[i][9+2] == "S":
            count += 1
        if mas[i][7+2] == "S":
            count += 1
        places.append([(i, 8+2), count])

    if mas[i][9+2] == ".":
        count = 0
        if mas[i][8+2] == "S":
            count += 1
        places.append([(i, 9+2), count])

c = 0
for i in range(len(mas)):

    if mas[i][0] == "S":
        if mas[i][1] == "P" or mas[i][1] == "S":
            c+=1

    if mas[i][1] == "S":
        if mas[i][2] == "P" or mas[i][2] == "S":
            c += 1
        if mas[i][0] == "P" or mas[i][0] == "S":
            c += 1

    if mas[i][2] == "S":
        if mas[i][1] == "P" or mas[i][1] == "S":
            c+= 1

    if mas[i][3+1] == "S":
        if mas[i][4+1] == "P" or mas[i][4+1] == "S":
            c+= 1

    if mas[i][4+1] == "S":
        if mas[i][3+1] == "P" or mas[i][3+1] == "S":
            c += 1
        if mas[i][5+1] == "P" or mas[i][5+1] == "S":
            c += 1

    if mas[i][5+1] == "S":
        if mas[i][4+1] == "P" or mas[i][4+1] == "S":
            c += 1
        if mas[i][6+1] == "P" or mas[i][6+1] == "S":
            c += 1

    if mas[i][6+1] == "S":
        if mas[i][5+1] == "P" or mas[i][5+1] == "S":
            c += 1

    if mas[i][7+2] == "S":
        if mas[i][8+2] == "P" or mas[i][8+2] == "S":
            c += 1

    if mas[i][8+2] == "S":
        if mas[i][9+2] == "P" or mas[i][9+2] == "S":
            c += 1
        if mas[i][7+2] == "P" or mas[i][7+2] == "S":
            c += 1

    if mas[i][9+2] == "S":
        if mas[i][8+2] == "P" or mas[i][8+2] == "S":
            c += 1

places.sort(key = lambda x : x[1])

for place in places[:k]:
    r , p = place[0]
    c += place[1]
    mas[r] = mas[r][:p] + 'x' + mas[r][p+1:]

print c
for line in mas:
    print line
