import sys

original_stdout = sys.stdout

var_for_sublime = False 


if var_for_sublime:
	fin = open('input.txt' , 'r')
	fout = open('output.txt' , 'w')
	sys.stdout = fout
	def give_string():
		return fin.readline()
else:
	fin = sys.stdin 
	fout = sys.stdout
	def give_string():
		return fin.buffer.readline()

def give_list():
	return list(map(int, give_string().split()))
def give_int():
	return int(give_string())


MOD = 1e9+ 7
def modpow( a , p) :
	ans = 1 
	while p :
		if p&1 :
			ans *= a 
			ans %= MOD 
		p >>= 1
		a *= a
		a %= MOD
	return ans 		



def fac_cal(n):
	ans = 1
	for i in range(1 , n+1):
		ans *= i 
		ans %= MOD 
	return ans 





def solve():
	n = give_int() 
	t=  give_list() 
	t.sort()
	# print(t)
	dp = [ [1e9]*(2*n) for i in range(n)]
	for i in range(n):
		for j in range(i+1 , 2*n):
			if(i == 0):
				dp[i][j] = min(dp[i][j-1] , abs(t[0] - j))
			else:
				dp[i][j] = min(dp[i][j-1] , dp[i-1][j-1]+abs(t[i]-j))
			# print(i , j )
	ans = 1e9
	# for li in dp:
	# 	print(li)
	for i in range(2*n):
		ans = min(ans , dp[n-1][i])
	print(ans)


def main():
	try :
		t = 1 
		t = give_int()
		for i in range(t):
			solve()
	finally:
		fin.close()
		fout.close()
		sys.stdout = original_stdout





if __name__ == '__main__' :
	main()
