from math import *
from sys import stdin,stdout

def binarySearch(arr,x,i):
	l=i
	r=len(arr)-1 
	while l <= r: 
		mid = (l + r)//2; 
		if arr[mid] == x: 
			return mid 
		elif arr[mid] < x: 
			l = mid + 1
		else: 
			r = mid - 1
	return -1

def js(arr,x):
	l=0
	r=len(arr)-1
	ans=-1
	while(l<=r):
		m=(l+r)//2
		if(arr[m]<=x):
			ans=m
			l=m+1
		else:
			r=m-1
	return ans

def jg(arr,x):
	l=0
	r=len(arr)-1
	ans=-1
	while(l<=r):
		m=(l+r)//2
		if(arr[m]>=x):
			ans=m
			r=m-1
		else:
			l=m+1
	return ans

def ceil(a,b):
	if a%b == 0:
		return int(a/b)
	else:
		return (a//b + 1)


def c(s,i,a,b):
	count=0
	for j in range(i,len(s)):
		if(s[i]!=s[j]):
			if(s[i]=="A"):
				count+=a
			else:
				count+=b
			i=j
	if(i<len(s)-1):
		if(s[i]=="A"):
			count+=a
		else:
			count+=b
	return count


