# Prints smaller elements on left  
# side of every element

from collections import deque 
def printPrevSmaller(arr, n): 
	S = deque()
	ind = deque()
	l = []  
	for i in range(n): 
		while (len(S) > 0 and S[-1] > arr[i]): 
			S.pop()
			ind.pop()  
		if (len(S) == 0): 
			l.append(-1) 
		else:
			l.append(ind[-1]) 
		S.append(arr[i])
		ind.append(i)
	return l


