def p1(n, lst):
    a = sorted(lst)
    for i in range(n-1):
        if a[i+1] - a[i] > 1:
            return "NO"
    return "YES"





def main():
    t = int(input())
    for i in range(t):
        n = int(input())
        lst = list(map(int, input().split()))
        if n == 1:
            print("YES")
            continue
        else:
            print(p1(n, lst))
main()
