#! /usr/bin/env python
# -*- coding: utf-8 -*-
# vim:fenc=tf-8
#

"""
"""

from operator import itemgetter
from math import sqrt
from collections import defaultdict


n = int(input())
a = list(map(int,input().split()))


def solve():
    log = defaultdict(int) 
    people = 0
    count = 0
    c = []
    for i in range(n):
        ai = a[i]
        sign = (ai > 0)
        ai = abs(ai)
        tmp = log[ai]
        if tmp == 0 and sign:
            log[ai] = 1
            people += 1
            count += 1
        elif tmp == 1 and not sign:
            log[ai] = 2
            people += -1
            count += 1
        else:
            return(-1,[])
        if people == 0:
            c.append(count)
            log = defaultdict(int) 
            people = 0
            count = 0
    if people == 0:
        return(1,c)
    else:
        return(-1,[])

yn, c = solve()
if yn == -1:
    print(-1)
else:
    print(len(c))
    print(' '.join(map(str,c)))
