import sys
n, m, d = [int(i) for i in input().split()]
c = [int(i) for i in input().split()]

a = []
i = 0
j = 0
while i + d < n + 1:
    if j >= len(c):
        print("NO")
        sys.exit()
    i += d + c[j] - 1
    a += [0] * (d - 1)
    a += [j+1] * (c[j])
    j += 1

s = 0
if len(a) > n:
    s += len(a) - n
elif len(a) < n:
    a += [0] * (n - len(a))

for i in range(j, m):
    s += c[i]

if s != 0:
    for i in range(n-1, -1, -1):
        if a[i] == 0:
            s -= 1
            if s == 0:
                break
    f = i
    a = a[:f]
    try:
        j = max(a)
    except:
        j = 0
    while j < m:
        a += [j+1]*(c[j])
        j += 1

print("YES")
for i in a:
    print(i, end=" ")
