#!/usr/bin/env python3
import sys

def rint():
    return map(int, sys.stdin.readline().split())
#lines = stdin.readlines()


def cnt_b(xx1, yy1, xx2, yy2):
    w = xx2 - xx1 + 1
    h = yy2 - yy1 + 1
    return w*h - cnt_w(xx1, yy1, xx2, yy2)


def cnt_w(xx1, yy1, xx2, yy2):
    w = xx2 - xx1 + 1
    h = yy2 - yy1 + 1
    cnt = (w*h)//2
    if w%2 and h%2:
        if xx1%2 and yy1%2 or xx1%2 == 0 and yy1%2 == 0:
            cnt+=1
    return cnt


def area(w, h):
    return w*h


def cross_rect(x1, y1, x2, y2, x3, y3, x4, y4):
    xl = max(x1, x3)
    xr = min(x2, x4)
    yl = max(y1, y3)
    yr = min(y2, y4)
    if xl <= xr and yl <= yr:
        return xl, yl, xr, yr
    else:
        return -1, -1, -1, -1


t = int(input())
for i in range(t):
    n, m = rint()
    x1, y1, x2, y2 = rint()
    x3, y3, x4, y4 = rint()
    tot_w = cnt_w(1, 1, m, n)
    wc = tot_w + cnt_b(x1, y1, x2, y2)
    wc -= cnt_w(x3, y3, x4, y4)
    xl, yl, xr, yr = cross_rect(x1, y1, x2, y2, x3, y3, x4, y4)
    if xl != -1:
        wc -= cnt_b(xl, yl, xr, yr)
    print(wc, m*n - wc)
