# coding: utf-8
def sol():
    n, cap = [int(x) for x in input().split(' ')]
    vec =sorted([int(x)for x in input().split(' ')])
    freq={}
    for i in vec:
        freq[i] = 0
    for i in vec:
        freq[i] +=1
    ans = 0
    while n>0:
        temp_c = cap
        n -= temp_c
        visited_floors = []
        while temp_c > 0:
            floor = 0
            for key,val in freq.items():

                if val > 0 and floor <= key:
                    floor = key
            if floor == 0:
                break            
            dropoff = min(temp_c,freq[floor])
            temp_c -= dropoff
            freq[floor]-= dropoff
            visited_floors.append(floor)

        max_floor_visited = max(visited_floors)

        for i in vec:
            if freq[i] == 0:
                freq[i] = -1   
        ans+=2*(max_floor_visited-1)
    print(ans)
    return ans
sol()
