k=int(input())
l=input()
if k==0:
    c=0
    ans=0
    for i in range(len(l)):
        if l[i]=="0":
            c+=1
        else:
            temp=((c*(c+1))//2)
            ans+=temp
            c=0
    ans+=(c*(c+1))//2
    print(ans)
else:
    ans=0
    ar=[]
    a=-1
    for i in range(len(l)):
        if l[i]=="1":
            ar.append(i-a-1)
            a=i
    ar.append(len(l)-a-1)
    p=len(ar)-1
    for i in range(len(ar)):
        if i+k>p:
            break
        ans+=ar[i]*ar[i+k]+ar[i]+ar[i+k]+1
    print(ans)
