def hi(a):
	if sum(a)==1:
		p = a.index(1)
		b = ["Tidak"]*4
		b[p] = "Ya"
		return b
	p = (-1)**(a[0]+a[1])
	b = [-2]*4
	if p<0:
		b[2],b[3] = "Tidak","Tidak"
		if a[0]==0 and a[3]==0:
			b[0],b[1] = "Tidak","Ya"
			return b
		if a[1]==0 and a[2]==0:
			b[1],b[0] = "Tidak","Ya"
			return b
		else:
			b[0],b[1] = "Ya","Ya"
			return b
	if p>0:
		b[0],b[1] = "Tidak","Tidak"
		if a[0]==0 and a[3]==0:
			b[3],b[2] = "Tidak","Ya"
			return b
		if a[1]==0 and a[2]==0:
			b[2],b[3] = "Tidak","Ya"
			return b
		else:
			b[2],b[3] = "Ya","Ya"
			return b


t = int(input())
b = []
for i in range(t):
	a = list(map(int,input().split()))
	b.append(hi(a))
for i in range(len(b)):
	b[i] = " ".join(b[i])
	print(b[i])
