n = int(input())
d = dict()
ans = []
arr = []
d2 = dict()
for i in range(n):
    a, b = input().split()
    a1, a2 = a[:3], a[0] + a[1] + b[0]
    if a1 in d:
        d[a1].append(a2)
    else:
        d[a1] = [a2]
    if a2 in d2:
        d2[a2].append((a1, i))
    else:
        d2[a2] = [(a1, i)]
    ans.append(a2)
    arr.append(a1)
for i in d:
    if len(set(d[i])) != len(d[i]):
        print('NO')
        exit()
for i in d2:
    if len(d2[i]) == 1:
        continue
    new_d = dict()
    for j in d2[i]:
        if j[0] in new_d:
            new_d[j[0]].append(j[1])
        else:
            new_d[j[0]] = [j[1]]
    for j in new_d:
        if len(new_d[j]) > 1:
            print('NO')
            exit()
    change = []
    for j in new_d:
        if len(d[j]) == 1:
            change.append(new_d[j][0])
    if len(change) < len(d2[i]) - 1:
        print('NO')
        exit()
    for j in change:
        ans[j] = arr[j][::]
if len(set(ans)) != len(ans):
    print('NO')
    exit()
print('YES')
print(*ans, sep='\n')
