n = int(input())

lines = [list(input()) for _ in range(n)]


def umeru(umeru_info, line):
    out_umeru_info = []
    ok = True
    for line_i, cell_i, count in umeru_info:
        if count == 1:
            if line[cell_i-1] == "." and line[cell_i] == "." and line[cell_i+1] == ".":
                line[cell_i-1] = "#"
                line[cell_i] = "#"
                line[cell_i+1] = "#"
                out_umeru_info.append([line_i, cell_i, 2])
            else:
                ok = False
                return ok, None, None
        else: #count == 2:
            if line[cell_i] == ".":
                line[cell_i] = "#"
            else:
                ok = False
                return ok, None, None

    return ok, out_umeru_info, line

def find_first(line_i, line, umeru_info, n):
    ok = True
    for cell_i, cell in enumerate(line):
        if cell == ".":
            if (cell_i in [0, n-1]) or line_i in [n-2, n-1]:
                ok = False
                return ok, None
            else:
                umeru_info.append([line_i, cell_i, 1])

    return ok, umeru_info

umeru_info = []
fail = False
for line_i, line in enumerate(lines):
    ok, umeru_info, line = umeru(umeru_info, line)
    if not ok:
        fail = True
        break

    ok, umeru_info = find_first(line_i, line, umeru_info, n)
    if not ok:
        fail = True
        break

out = "NO" if fail else "YES"
print(out)
