n, m = map(int, input().split())


dishes = [0 for _ in range(n + m)]
father = [-1 for _ in range(n + m)]
e_out = dict()
v_in = [0 for _ in range(n + m)]


def get_father(n):
    if father[n] == -1:
        return n
    else:
        father[n] = get_father(father[n])
        return father[n]


compare_matrix = []
for i in range(n):
    compare_matrix.append(input())

for i in range(n):
    for j in range(m):
        if compare_matrix[i][j] == "=":
            fi = get_father(i)
            fj = get_father(j + n)
            if fi != fj:
                father[fj] = fi
children = dict()
for i in range(n + m):
    fi = get_father(i)
    if fi != i:
        if fi not in children:
            children[fi] = [i]
        else:
            children[fi].append(i)

for i in range(n):
    for j in range(m):
        if compare_matrix[i][j] == "=":
            continue
        fi = get_father(i)
        fj = get_father(j + n)
        if fi == fj:
            print("NO")
            exit(0)
        if compare_matrix[i][j] == ">":
            v_in[fi] += 1
            if fj in e_out:
                e_out[fj].append(fi)
            else:
                e_out[fj] = [fi]
        if compare_matrix[i][j] == "<":
            v_in[fj] += 1
            if fi in e_out:
                e_out[fi].append(fj)
            else:
                e_out[fi] = [fj]
# print(v_in)
# print(e_out)
score = 1
visited = [False for _ in range(n + m)]
v_total = 0
q = [v for v in range(n + m) if v_in[v] == 0]
while q:
    t = []
    for i in q:
        dishes[i] = score
        v_total += 1
        if i in children:
            for j in children[i]:
                dishes[j] = score
                v_total += 1
        if i in e_out:
            for j in e_out[i]:
                v_in[j] -= 1
                if v_in[j] == 0:
                    t.append(j)
    q = t
    score += 1

if v_total < n + m:
    print("NO")
    exit(0)
print("YES")
for i in dishes[:n]:
    print(i, end=" ")
print()
for i in dishes[n:n + m]:
    print(i, end=" ")
