import os
import sys
from atexit import register
from io import BytesIO
sys.stdin = BytesIO(os.read(0, os.fstat(0).st_size))
sys.stdout = BytesIO()
register(lambda: os.write(1, sys.stdout.getvalue()))
input = lambda: sys.stdin.readline().rstrip('\r\n')

chars = list("abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789")
T = int(input())
def do(c):
	if c == ".":
		return 0
	else:
		return 1

for _ in range(T):
	r,c,k = map(int,input().split(" "))
	mat = []
	for i in range(r):
		mat.append(map(do,input()))
	ssum = 0
	for i in range(r):
		ssum += sum(mat[i])
	th = [ssum/k]*k
	diff = ssum - (ssum/k)*k
	for i in range(diff):
		th[i] += 1
	for i in range(1,k):
		th[i] += th[i-1]
	cols = [range(c)]+[range(c)[::-1]]
	scnt = 0
	ncnt = 0
	ret = [['0']*c for i in range(r)]


	for i in range(r):
		for j in cols[i%2]:
			ret[i][j] = chars[ncnt]
			if mat[i][j] == 1:
				scnt += 1
				if scnt == th[ncnt] and ncnt<k-1:
					ncnt += 1
	for i in range(r):
		print "".join(ret[i])
