import sys


t =sys.stdin.readline()
t = int(t)

for tttt in range(t) :
  org=[]
  dic={}
  n =sys.stdin.readline()
  n = int(n)
  users=sys.stdin.readline().split()
  users=['?']+users
  for k in range(n+1):
     dic[users[k]]=k
  m = sys.stdin.readline()
  m=int(m)
  messusers=[]
  messages=[]
  for mmmm in range(m):
     curr=[]
     ms=sys.stdin.readline().strip()
     org.append(ms)
     pos=ms.find(':')
     name=ms[:pos]
     ms=' '+ms[pos+1:]+' '
     ms=ms.replace('.',' ')
     ms=ms.replace('?',' ')
     ms=ms.replace(',',' ')
     ms=ms.replace('!',' ')
     messusers.append(dic[name])
     for nm in users:
        nmm=' '+nm+' '
	if nmm in ms:
	   curr.append(dic[nm])
     messages.append(curr)

  m=len(messages)
  found=True
  for i in range(m):
    if messusers[i]!=0:
	continue
    now=[]
    if i > 0 :
	now.append(messusers[i-1])
    if i < m-1 and messusers[i+1]!=0:
        now.append(messusers[i+1])
    now+=messages[i]
    cand=[]
    for us in range(1,len(users)):
	if us not in  now:
	    cand.append(us)
    if len(cand)==0 :
        found=False
        break
    else:
	messusers[i]=cand[0]
 	#filter
	filter=[]
	if i<m-1:
	  filter+=messages[i+1]
        if i<m-2 and messusers[i+2]!=0:
	  filter.append(messusers[i+2])
        for k in cand:
	  if k in filter:
	     messusers[i]=k
             break
	org[i]=users[messusers[i]]+org[i][1:]
  if found:
    for ms in org:
      print ms
  else:
    print 'Impossible'
