# http://codeforces.com/problemset/problem/1316/D

from collections import deque

n = int(input())

b = [None]*(n*n)
m = [None]*(n*n)
x = 0

for r in range(n):
    pq = list(map(int, input().split()))
    for c in range(n):
        rr = pq[2*c] - 1
        rc = pq[2*c+1] - 1

        if rr != -2:
            x += 1
            b[r*n+c] = (rr, rc)

def bfs(sr, sc):
    global x
    m[sr*n+sc] = "X"
    x -= 1
    q = deque()
    q.append((sr, sc))
    while len(q) != 0 and x != 0:
        r, c = q.popleft()
        ne = [
            ((r-1, c), "D"),
            ((r+1, c), "U"),
            ((r, c-1), "R"),
            ((r, c+1), "L"),
        ]
        for el, co in ne:
            r, c = el
            if r not in {-1, n} and c not in {-1, n} and b[r*n+c] == (sr, sc) and m[r*n+c] is None:
                m[r*n+c] = co
                x -= 1
                q.append(el)

for r in range(n):
    for c in range(n):
        be = b[r*n+c]
        if be == (r, c):
            bfs(r, c)
            continue
        if be is not None:
            continue
        if r > 0 and b[(r-1)*n+c] is None:
            m[r*n+c] = "U"
        elif r < n-1 and b[(r+1)*n+c] is None:
            m[r*n+c] = "D"
        elif c > 0 and b[r*n+c-1] is None:
            m[r*n+c] = "L"
        elif c < n-1 and b[r*n+c+1] is None:
            m[r*n+c] = "R"
        else:
            print("INVALID")
            exit()

if x != 0:
    print("INVALID")
    exit()

print("VALID")
for r in range(n):
    print("".join(m[r*n:r*n+n]))
