n = int(input())
init = list(map(int, input().split()))
k = int(input())
after = list(map(int, input().split()))

prev, cur, t = -1, 0, 0
ans = []

if sum(init) != sum(after):
    print("NO")
    exit()

for i in range(n):
    t += init[i]
    if t == after[cur] and cur < k:
        cur += 1
        t = 0
        ans.append((prev + 1, i))
        prev = i

if cur != k:
    print("NO")
else:
    res = []
    for i in range(len(ans)):
        s, f = ans[i][0], ans[i][1]
        if s == f: continue

        mx = max(init[s:f+1])

        j = -1
        l = True
        for ii in range(s, f + 1):
            if init[ii] == mx:
                if ii - 1 >= s and init[ii] > init[ii - 1]:
                    j = ii
                    l = True
                    break
                if ii + 1 <= f and init[ii] > init[ii + 1]:
                    j = ii
                    l = False
                    break

        if j == -1:
            print("NO")
            exit()

        if l:
            for ij in reversed(range(2, j - s + 2)):
                res.append((i + ij, 'L'))

            for _ in range(f - j):
                res.append((i + 1, 'R'))
        else:
            for _ in range(f - j):
                res.append((i + j - s + 1, 'R'))

            for ij in reversed(range(2, j - s + 2)):
                res.append((i + ij, 'L'))

    print("YES")
    for _ in range(len(res)):
        print(*res[_])
