def solve(candies,start,n):
    #print(start)
    time = 0
    train = {}
    while True:
        far = 0
        for i in candies:
            if i[0] == start:
                if i[1] < start:
                    far = max(far,i[1]+n)
                else:
                    #print(far,i[1])
                    far = max(far,i[1])

        if far != 0:
            if far > n:
                far -= n
            i = (start,far)
            if i not in train.keys():
                train[i] = 1
            else:
                train[i] += 1
        new = []
        for i in candies:
            if i[1] == start:
                if i in train.keys():
                    train[i] -= 1
                    if train[i] == 0:
                        del train[i]
                else:
                    new.append(i)
            else:
                new.append(i)

        candies = new[:]
        #print(candies,train,start)
        if not candies:
            break
        time += 1
        start += 1
        if start == n+1:
            start = 1


    return time

def main():
    n,m = map(int,input().split())
    stations = {}
    for i in range(1,n+1):
        stations[i] = []

    candies = []
    for i in range(m):
        a,b = map(int,input().split())
        candies.append((a,b))

    ans = []

    for i in range(n):
        ans.append(solve(candies[:],i+1,n))

    for i in ans:
        print(i,end = ' ')


main()
