n = int(raw_input())
domain_queries = {}
for i in range(n):
    address = raw_input().strip()[7:]
    if '/' not in address:
        domain = address
        query = ''
    else:
        pos = address.index('/')
        domain = address[:pos]
        query = address[pos:]
    domain_queries.setdefault(domain, set()).add(query)
domain_signatures = {}
for domain, queries in domain_queries.items():
    domain_signatures[domain] = ','.join(sorted(queries))
signature_domains = {}
for domain, signature in domain_signatures.items():
    signature_domains.setdefault(signature, []).append('http://' + domain)
lines = []
for signature, domains in signature_domains.items():
    if len(domains) == 1:
        continue
    lines.append(' '.join(domains))
print(len(lines))
if len(lines) > 0:
    print('\n'.join(lines))
