import sys;input=sys.stdin.readline
N, = map(int, input().split())
X = [0]+list(map(int, input().split()))
Y = [0]+list(map(int, input().split()))
st = []
G = [set() for _ in range(N+1)]
for i in range(1, N+1):
    if Y[i] < 0:
        st.append(i)
    else:
        G[Y[i]].add(i)

ato = []
R = []
W = [0]*(N+1)
for s in st:
    stack = [s]
    vs_dfs = list()
    while stack:
        v = stack.pop()
        vs_dfs.append(v)
        for u in G[v]:
#            vs.add(u)
            stack.append(u)
    for v in vs_dfs[::-1]:
        for u in G[v]:
            X[v] += max(0, X[u])
        if X[v] < 0:
            ato.append(v)
        else:
            R.append(v)
ato = ato[::-1]
print(sum(X))
print(*(R+ato))
