        self.number, self.wayLen, self.wayTime, self.preNode = number, wayLen, wayTime, preNode

class Path:
    def __init__(self, wayLen, wayTime, nextNode):
        self.wayLen, self.wayTime, self.nextNode = wayLen, wayTime, nextNode 

