    cNode = endNode
    while cNode.preNode != None:
        tailLen += 1
        tailTime += cities[cNode.preNode.number][cNode.number]
        b = memo[cNode.preNode.number].Insert(Path(tailLen, tailTime, cNode.number))
        if not b:
