from heapq import heappush, heappop
from collections import defaultdict

def solve(start, lt):
    n = len(lt)
    ii = list(sorted(set(lt)))
    maxlt = max(ii)
    right = {}
    left = {}
    leftn = {}
    rightn = {}
    last = {}

    for i in range(2 * n):
        l = lt[i % n]
        if l in last:
            right[last[l]] = i % n
            left[i % n] = last[l]
        last[l] = i % n

    hole = defaultdict(lambda: - 10**10)
    _mindst = {}
    mindstb = {}
    mindste = {}
    for i in range(n):
        hole[lt[i]] = max(hole[lt[i]], (right[i] - i) % n)
    for i in range(n):
        _mindst[lt[i]] = n - hole[lt[i]]
    last = 0
    for i in reversed(ii):
        mindste[i] = last
        last += _mindst[i]
        mindstb[i] = last

    def np(state):
        pos, boe = state
        if boe == 'begin':
            if left[pos] == pos:
                yield (0, (pos, 'end'), (('>', pos),))
            l = pos
            while True:
                ll = left[l]
                if ll == pos:
                    break
                distance = 2 * ((pos - l) % n) + (ll - pos) % n
                path = (('<', l), ('>', ll))
                yield (distance, (ll, 'end'), path)
                l = left[l]
            r = pos
            while True:
                rr = right[r]
                if rr == pos:
                    break
                distance = 2 * ((r - pos) % n) + (pos - rr) % n
                path = (('>', r), ('<', rr))
                yield (distance, (rr, 'end'), path)
                r = right[r]
        if boe == 'end' or boe == 'start':
            if boe == 'start':
                nxtl = ii[0]
            else:
                nxtl = ii[ii.index(lt[pos]) + 1]
            p = pos
            while lt[p] != nxtl:
                p = (p + 1) % n
            yield ((p - pos) % n, (p, 'begin'), (('up', '>', p),))
            p = pos
            while lt[p] != nxtl:
                p = (p - 1) % n
            yield ((pos - p) % n, (p, 'begin'), (('up', '<', p),))

    def dijkstra(beg):
        border = []
        heappush(border, (mindstb[lt[beg[0]]], 0, beg, None, None))
        fin = {}
        while True:
            asd, verd, ver, parent, path = heappop(border)
            if ver not in fin:
                fin[ver] = verd, parent, path
                pos, boe = ver
                if boe == 'end' and lt[pos] == maxlt:
                    cur = ver
                    gpath = []
                    while cur != beg:
                        _, parent, path = fin[cur]
                        gpath.extend(reversed(path))
                        cur = parent
                    return verd, list(reversed(gpath))
                for d, neigh, path in np(ver):
                    pos, boe = neigh
                    if boe == 'start' or boe == 'begin':
                        asd = d + verd + mindstb[lt[pos]]
                    else:
                        asd = d + verd + mindste[lt[pos]]
                    heappush(border, (asd,(d + verd), neigh, ver, path))

    def reconstruct(path):
        cur = start
        res = []
        done = set()
        for ps in path:
            #print('ps', ps, 'cur', cur)
            if len(ps) == 3:
                _, direction, pos = ps
                dst = (pos - cur) % n if direction == '>' else (cur - pos) % n
                sign = '+' if dst == 0 or direction == '>' else '-'
                res.append('%s%d'%(sign, dst))
                cur = pos
                done.add(pos)
            else:
                direction, pos = ps
                if direction == '>':
                    last = cur
                    while cur != pos:
                        cur = right[cur]
                        if cur not in done:
                            done.add(cur)
                            res.append('+%d'%((cur-last)%n))
                            last = cur
                if direction == '<':
                    last = cur
                    while cur != pos:
                        cur = left[cur]
                        if cur not in done:
                            done.add(cur)
                            res.append('-%d'%((last-cur)%n))
                            last = cur
        return res

    dst, path = dijkstra((start, 'start'))
    return dst, reconstruct(path)

def check(start, lt, sol, dst):
    cmp_dst, path = sol
    ltcur = -10**10
    n = len(lt)
    assert(cmp_dst == dst)
    done = set()
    cur = start
    for move in path:
        cur += {'+': 1, '-': -1}[move[0]] * int(move[1:])
        cur %= n
        assert(cur not in done)
        done.add(cur)
        assert(lt[cur] >= ltcur)
        ltcur = lt[cur]
    assert(len(done) == len(lt))

