def cmpr(E):
    global ans
    ans = [tmpl] * fam
    for e in E:
        res = eval(e.replace('!', '~')) & (fam - 1)
        if len(ans[res]) > len(e) or len(ans[res]) == len(e) and ans[res] > e:
            ans[res] = e
    return set(ans) - {tmpl}

