import sys
sys.setrecursionlimit(100000)

def dfs(v, l, r, mark):
    if v == n - 1:
        return True
    mark[v] = True
    for u, e in graph[v]:
        if not mark[u] and edges[e][0] <= l <= r <= edges[e][1]:
            if dfs(u, l, r, mark):
                return True
    return False


def pred(k):
    for l, _ in edges:
        if dfs(0, l, l + k - 1, [False] * n):
            return True
    return False


n, m = map(int, raw_input().split())
graph = [[] for _ in range(n)]
edges = []

maxd = 0
for i in range(m):
    ak, bk, lk, rk = map(int, raw_input().split())
    edges.append((lk, rk))
    graph[ak - 1].append((bk - 1, i))
    graph[bk - 1].append((ak - 1, i))
    maxd = max(maxd, rk - lk + 1)


l = 0
r = maxd + 1
while r - l > 1:
    mid = (l + r) / 2
    if pred(mid):
        l = mid
    else:
        r = mid
if l > 0:
    print(l)
else:
    print('Nice work, Dima!')
