		slack+=1
	elif el > a:
		if cn < bn:
			cn+=1
			if cn > m:
				m = cn
			if slack > 0:
				slack -= 1
				continue
		else:
			bn+=1
			if bn > m:
				m = bn
	else:
		if cn < bn and cn < an:
			cn+=1
			if cn > m:
				m = cn
			if slack > 0:
				slack -= 1
				continue
		elif bn < an:
			bn+=1
			if bn > m:
				m = bn
		else:
			an+=1
			if an > m:
				m = an
	k+=1

#print an,bn,cn
if slack > 0:
	mprev = max(mprev, an, bn)
	slack -= abs(mprev-cn)
	if slack > 0:
