def bs_ceil(arr, val):
    l, r = -1, len(arr)
    # 0 to n
    while r - l > 1:
        mid = (l + r) / 2
        if arr[mid][0] >= val:
            r = mid
        else:
            l = mid
    return r


n, m, k = map(int, raw_input().split())
D = [float('inf')] * (n + 1)
A = [float('inf')] * (n + 1)
arr1 = []
arr2 = []
for i in xrange(m):
    d, s1, s2, c = map(int, raw_input().split())
    if s2 == 0:
        arr1.append((d, s1, c))
    if s1 == 0:
        arr2.append((d, s2, c))
arr1.sort()
arr2.sort()

n1, n2 = len(arr1), len(arr2)
f, tc = 0, 0
a1, a2 = [], []
for i in xrange(n1):
    d, s1, c = arr1[i]
    if D[s1] == float('inf'):
        D[s1] = c
        f, tc = f + 1, tc + D[s1]
    else:
        tc -= D[s1]
        D[s1] = min(D[s1], c)
        tc += D[s1]
    if f == n and (a1 == [] or a1[-1][1] > tc):
        a1.append((d, tc))

f, tc = 0, 0
for i in reversed(xrange(n2)):
    d, s1, c = arr2[i]
    if A[s1] == float('inf'):
        A[s1] = c
        f, tc = f + 1, tc + A[s1]
    else:
        tc -= A[s1]
        A[s1] = min(A[s1], c)
        tc += A[s1]
    if f == n and (a2 == [] or a2[-1][1] > tc):
        a2.append((d, tc))

#print a1, a2
a2 = a2[::-1]
n1, n2 = len(a1), len(a2)
ans = float('inf')
if len(a2) > 0:
    for i in xrange(n1):
        d, c = a1[i]
        p = bs_ceil(a2, d + k + 1)
        if p != len(a2):
            ans = min(ans, c + a2[p][1])
if ans == float('inf'):
    print -1
else:
    print ans
