class Joe(object):
    def __init__(self, map):
        self.x = 1
        self.y = 0
        self.map = [list('#' + floor + '#') for floor in map]
        self.dir = 1

    def get_floor_time(self, floor, next_floor):
        iter = 0
        time = 0
        empty = 0
        wall = {-1: False, 1: False}
        fl = len(floor)

        while not (wall[-1] and wall[1]):
            if next_floor[self.x] == '.':
                return time
            next_x = self.x + self.dir

            #for i in range(fl):
            while floor[next_x] == '.':
                self.x = next_x
                empty += 1
                time += 1
                if next_floor[self.x] == '.':
                    return time
                next_x = self.x + self.dir

            if floor[next_x] == '+':
                floor[next_x] = '.'
            elif floor[next_x] == '#':
                wall[self.dir] = True
            self.dir *= -1
            if wall[self.dir]:
                time += empty * 2 + 2
                self.dir *= -1
            else:
                self.x += self.dir * empty
                time += 1 + empty
        return None

    def get_time(self):
        res_time = 0
        while self.y < len(self.map) - 1:
            time = self.get_floor_time(self.map[self.y], self.map[self.y + 1])
            if time is None:
                return 'Never'
            res_time += time + 1
            self.y += 1

        return res_time

