from collections import Counter

def is_prime(x):
    if x < 2:
        return 0
    for i in range(2, x):
        if x % i == 0:
            return False
    return True

def proc(s):
    n = len(s)
    same = set()
    for p in range(2,n+1):
        if not is_prime(p):
            continue
        if p * 2 > n:
            continue
        for i in range(2, n//p+1):
            same.add(p*i)
        same.add(p)

    counter = Counter(s)
    ch, count = counter.most_common(1)[0]

    if count < len(same):
        print("NO")
        return

    same = [x-1 for x in same]
    w = [x for x in s]
    for i in same:
        if w[i] == ch:
            continue
        for j in range(n):
            if j not in same and w[j] == ch:
                tmp = w[j]
                w[j] = w[i]
                w[i] = tmp
                break

    print("YES")
    print(''.join(w))

s = input()
proc(s)
