for _ in range(int(input())):
    a, b = map(int, input().split())
    ans = b - a
    for i in range(0,a+1):
        if (a+i)|b==b:
            ans = min(ans, i+1)
            break
    for i in range(b+1):
        if (b+i)|a==(b+i):
            ans = min(ans,i+1)
            break
    print(ans)
