import sys

input = sys.stdin.buffer.readline


def main():
    n = int(input())
    a, b = [], []
    for _ in range(n):
        ai, bi = map(int, input().split())

        a.append(ai)
        b.append(bi)

    def good(x):
        count = 0
        for i in range(n):
            if a[i] >= x-count-1 and b[i] >= count:
                count += 1
        return count>=x

    l, r = -1, n+1
    while abs(l-r) > 1:
        mid = (l+r)//2
        if good(mid):
            l = mid
        else:
            r = mid

    return str(l)


if __name__ == "__main__":
    print("\n".join([main() for _ in range(int(input()))]))
