import sys

input = sys.stdin.readline


############ ---- Input Functions ---- ############
def inp():
    return (int(input()))


def inlt():
    return (list(map(int, input().split())))


def insr():
    s = input()
    return (list(s[:len(s) - 1]))


def inst():
    s = input()
    return s[:len(s) - 1]


def invr():
    return (map(int, input().split()))


t = inp()

dr = [1, 0, -1, 0]
dth = [0, 1, 0, -1]


def solve():
    n = inp()
    m = [[True for j in range(720)] for i in range(40)]
    nvis = [[True for j in range(720)] for i in range(40)]
    for _ in range(n):
        cmd = inst().split()
        if cmd[0] == 'C':
            r = (int(cmd[1]) - 1) * 2 + 1
            th1 = (int(cmd[2]) * 2) % 720
            th2 = (int(cmd[3]) * 2) % 720
            while th1 != th2:
                m[r][th1] = False
                th1 = (th1 + 1) % 720
            m[r][th1] = False
        if cmd[0] == 'S':
            r1 = (int(cmd[1]) - 1) * 2 + 1
            r2 = (int(cmd[2]) - 1) * 2 + 1
            th = int(cmd[3]) * 2
            while r1 != r2:
                m[r1][th] = False
                r1 = r1 + 1
            m[r1][th] = False
    q = [(0, 0)]
    while len(q) > 0:
        pt = q.pop()
        for i in range(4):
            npt = (pt[0] + dr[i], (pt[1] + dth[i]) % 720)
            if npt[0] >= 0 and npt[0] < 40 and m[npt[0]][npt[1]] and nvis[
                    npt[0]][npt[1]]:
                q.append(npt)
                nvis[npt[0]][npt[1]] = False
    for i in range(720):
        if nvis[39][i] == False:
            print('YES')
            return
    print("NO")


for _ in range(t):
    solve()
