from __future__ import annotations

imp = "IMPOSSIBLE"

def bottleArrangements(tasters : list[(tuple[(int,int)])], n_bottles : int)  -> str:
    '''
        - tasters: list of preferences of tasters (Ri,Wi)
        - n_bottles: number of bottles
    '''
    nw = 0
    nr = 0
    s = ""
    tasters.sort(key= lambda e : sum(e))
    for (r,w) in tasters:
        r_aux = r - nr
        w_aux = w - nw

        if(r_aux < 0):
            r_aux = 0

        if(w_aux < 0):
            w_aux = 0

        if(nw+nr+r_aux+w_aux > n_bottles):
            return imp

        for _ in range(r_aux):
            nr +=1

            if(len(s) > 0 and s[0] == 'R'):
                s = 'R' + s
            else:
                s = s +'R'

        for _ in range(w_aux):
            nw += 1
            if(len(s) >0 and s[0] == 'W'):
                s = 'W' + s
            else:
                s = s +'W'

    if(len(s) < n_bottles):
        x = n_bottles - len(s)
        s = s + 'W'*x

    return s



n_test = int(input())
for i in range(n_test):
    n_bottle, n_taster = input().split()
    tasters = []
    for _ in range(int(n_taster)):
        r,w = input().split(" ")
        tasters.append((int(r),int(w)))

    print(bottleArrangements(tasters, int(n_bottle)))
