def solve():
    n = int(input())
    e = list(input())
    g = list(input())
    e.insert(0,"0")
    e.insert(n+1,"0")
   # print(e)
 
    
    
   
    c = 0
    for i in range(0,n):
        if g[i] == "1":
            if e[i+1] == "0":
                c+=1
            elif e[i] =="1":
                c+=1
                e[i] = "2"
            elif e[i+2] =="1":
                c+=1
                e[i+2] = "2"
    print(c)
                
           
case = int(input())
for _ in range(case):
   solve()
