def power(bottom, up):
    # print(bottom, up)
    if up == 1:
        return bottom
    elif up == 0:
        return 1
    tmp = power(bottom, int(up // 2))
    if up % 2 == 0:
        return (tmp * tmp)
    else:
        return (tmp * tmp * bottom)


def dfs(root):

    if root * 2 >= len(li):
        return {"ans" : 1 , "before": li[root]}

    left = dfs(root * 2)
    right = dfs(root * 2 + 1)

    tmp = {"ans": 0, "before" : ""}

    tmp["ans"] = left["ans"] * right["ans"]

    left["before"] = li[root * 2] + left["before"]
    right["before"] = li[root * 2 + 1] + right["before"]

    if left["before"] != right["before"]:
        tmp["ans"] *= 2
    
    tmp["ans"] %= mod

    if left["before"] <= right["before"]:
        tmp["before"] = left["before"] + right["before"]
    else:
        tmp["before"] = right["before"] + left["before"]
    # print(root , tmp)
