import sys
input=sys.stdin.readline
def unionfind(u):
    v=u
    while u!=parent[u]:
        u=parent[u]
    while v!=u:
        w=parent[v]
        parent[v]=u
        v=parent[w]
    return u
def path(u,v):
    s=[u]
    pre=[-1]*(n+1)
    pre[u]=u
    while s:
        x=s.pop()
        for y in g[x]:
            if pre[y]>=0:
                continue
            pre[y]=x
            s.append(y)
    ans=[v]
    while ans[-1]!=u:
        ans.append(pre[ans[-1]])
    return ans[::-1]
n,m=map(int,input().split())#30w 30w
g=[[] for i in range(n+1)]
parent=[i for i in range(n+1)]
for i in range(m):
    u, v = map(int, input().split())
    root1,root2=unionfind(u),unionfind(v)
    if root1==root2:
        continue
    parent[max(root1,root2)]=min(root1,root2)
    g[u].append(v)
    g[v].append(u)
l=len(g)
q=int(input())#n*q 30w
query=[]
d={}
for i in range(q):
    a,b=map(int,input().split())
    query.append([a,b])
    if a in d:
        del d[a]
    else:
        d[a]=1
    if b in d:
        del d[b]
    else:
        d[b]=1
if len(d)>0:
    print("NO")
    print(len(d)//2)
else:
    print("YES")
    for i in range(q):
        ans=path(query[i][0],query[i][1])
        print(len(ans))
        print(" ".join(map(str,ans)))
