import io
import os
import sys


def pred_grid(grid, n, m):
    cross = ord("X")
    blocking_cols = {}
    last_blocking = 0
    for col in range(m - 1):
        blocking = False
        for row in range(1, n):
            if (grid[row][col] == cross) and (grid[row - 1][col + 1] == cross):
                blocking = True
                break

        if blocking:
            for k in range(last_blocking, col + 1):
                blocking_cols[k] = col + 1
            last_blocking = col + 1

    return blocking_cols


input = io.BytesIO(os.read(0, \
         os.fstat(0).st_size)).readline


n, m = map(int, input().decode().split(" "))
if m == 1:
    for _ in range(n):
        input()

    q = int(input())
    for _ in range(q):
        input()
        sys.stdout.write("YES\n")
else:
    grid = [None] * n
    for i in range(n):
        grid[i] = input()
    blocking_cols = pred_grid(grid, n, m)

    q = int(input())
    for _ in range(q):
        x1, x2 = map(int, input().decode().split(" "))
        x1 -= 1
        x2 -= 1
        if blocking_cols.get(x1, m + 1) > x2:
            sys.stdout.write("YES\n")
        else:
            sys.stdout.write("NO\n")
