def islucky(num,k):
    f=False
    while num>0:
        r=num%10
        if r==k:
            f=True
            break
        num=num//10
    return f

t=int(input())
for _ in range(t):
  n,k=map(int,input().split())
  ar=list(map(int,input().split()))
  for x in range(n):
    num=ar[x]
    if num>=10*k:
        print("YES")
    else:
        x=0
        s=num 
        m=False
        while x<num:
            if islucky(x,k) and islucky(s,k):
                m=True
                break
            elif x%k==0 and islucky(s,k):
                m=True
                break
            elif islucky(x,k) and s%k==0:
                m=True
                break
            elif x%k==0 and s%k==0:
                m=True
                break
            else:
                x+=1 
                s-=1 
        if m:
            print("YES")
        else:
            print("NO")
