from sys import stdin
input=lambda :stdin.readline()[:-1]

def solve():
  n,m=map(int,input().split())
  a=list(set(map(int,input().split())))
  n=len(a)
  exist=[0]*(m+1)
  for i in a:
    exist[i]=1
  dp=list(range(m+1))
  cnt=0
  ans=10**9
  mx_cnt=[0]*(m+1)
  mx=m
  for i in range(m,0,-1):
    if exist[i]:
      cnt+=1
      mx_cnt[i]+=1
    for j in range(i*i,m+1,i):
      y=dp[j//i]
      if y<dp[j]:
        if exist[j]:
          mx_cnt[dp[j]]-=1
          mx_cnt[y]+=1
        dp[j]=y
    if cnt==n:
      while mx_cnt[mx]==0:
        mx-=1
      ans=min(ans,mx-i)
  print(ans)      

for _ in range(int(input())):
  solve()
