t= int(input())
for _ in range(t):
    n=int(input())
    P = [i for i in range(1,2**n+1)]
    while n>1:
        k=[]
        for i in range(0,2**(n)-1,4):
            print("? ", P[i], P[i+2], flush =True)
            a = int(input())
            if a==0:
                k.extend([P[i+1],P[i+3]])
            elif a==1:
                k.extend([P[i], P[i+3]])
            elif a ==2:
                k.extend([P[i+2],P[i+1]])
        n-=1
        P=k
    print("? ", P[0],P[1],flush =True)
    a=int(input())
    if a ==1:print("!",P[0],flush =True)
    else:print("!",P[1],flush =True)
