from collections import Counter
from sys import stdin, gettrace

if gettrace():
    def inputi():
        return input()
else:
    def input():
        return next(stdin)[:-1]


    def inputi():
        return stdin.buffer.readline()


def solve(n: int, ds: [[int]]) -> (bool, int, int, [[int]], [str], [str]):
    adj = [Counter() for _ in range(2 * n + 1)]
    for u, v in ds:
        adj[u][v] += 1
        adj[v][u] += 1
    visited = [False] * (2 * n + 1)
    cycles = []
    for r in range(1, 2 * n + 1):
        if not adj[r]:
            continue
        cycles.append([r])
        stack = []
        v = r
        while adj[r] or stack:
            visited[v] = True
            if adj[v]:
                a,c = adj[v].popitem()
                if a == v:
                    c //= 2
                del adj[a][v]
                if visited[a]:
                    cycles[-1] += [a, v]*c
                else:
                    cycles[-1] += [a, v]*(c-1)
                    cycles[-1].append(a)
                    stack.append(v)
                    v = a
            else:
                v = stack.pop()
                cycles[-1].append(v)
        cycles[-1].pop()
        if len(cycles[-1]) == 2:
            return False, None, None, None, None, None
    grid = [[] for _ in range(2)]
    pc = [[] for _ in range(2)]
    mc = [[] for _ in range(2)]
    for cycle in cycles:
        l = len(cycle) // 2
        grid[0] += cycle[:l]
        grid[1] += cycle[:l - 1:-1]
        pc[0] += ['L', 'R'] * (l // 2) + (['U'] if l % 2 == 1 else [])
        pc[1] += ['L', 'R'] * (l // 2) + (['D'] if l % 2 == 1 else [])
        mc[0] += ['U'] + ['L', 'R'] * ((l - 1) // 2) + (['U'] if l % 2 == 0 else [])
        mc[1] += ['D'] + ['L', 'R'] * ((l - 1) // 2) + (['D'] if l % 2 == 0 else [])
    return True, 2, n, grid, pc, mc


def main():
    n = int(inputi())
    ds = [[int(a) for a in inputi().split()] for _ in range(n)]
    ok, m, k, grid, pc, mc = solve(n, ds)
    if not ok:
        print(-1)
    else:
        print(m, k)
        for r in grid:
            print(' '.join(map(str, r)))
        for r in pc:
            print(''.join(r))
        for r in mc:
            print(''.join(r))


if __name__ == "__main__":
    main()
