import copy
t = int(input())
for _ in range(t):
    n = int(input())
    ass = sorted(list(map(int, input().split())))
    for k in range(n, 0, -1):
        l = copy.deepcopy(ass)
        for i in range(1, k + 1):
            while len(l) > 0 and l[-1] > k - i + 1:
                l.pop()
            if len(l) == 0:
                break
            else:
                l.pop()
                if len(l) > 0:
                    l.pop(0)
        else:
            print(k)
            break
    else:
        print(0)
